.tree {
  font-family: system-ui;
  line-height: 1.5em;
  margin: 0;
  padding: 0;
}

.tree ul {
  list-style-type: none;
  padding-left: 20px;
  position: relative;
}

.tree li {
  margin: 0;
  padding: 10px 5px 0 5px;
  position: relative;
}

.tree li::before,
.tree li::after {
  content: "";
  left: -20px;
  position: absolute;
  right: auto;
}

.tree li::before {
  border-left: 1px solid #ccc;
  bottom: 50px;
  height: 100%;
  top: 0;
  width: 1px;
}

.tree li::after {
  border-top: 1px solid #ccc;
  height: 20px;
  top: 15px;
  width: 25px;
}

.tree li:last-child::before {
  height: 30px;
}

.tree-icon {
  margin-right: 5px;
  cursor: pointer;
}

.tree-node {
  display: inline-block;
}

.tree-node:hover {
  background: #f3f3f3;
  border-radius: var(--appsmith-theme-borderRadius);
  transition: all 0.3s ease;
}

