customEChartConfig:
{{ ((chartType) => ( 
(() => {
  const data = CountBCByLoaiBC.data;

  const loaiList = [...new Set(data.map(item => item.loai))];
  const capList = [...new Set(data.map(item => item.cap))];

  return {
    xAxis: {
      type: "category",
      data: loaiList,
      axisLabel: {
        interval: 0,
        rotate: 20,
				margin: 8,
        formatter: function(value) {
          return value.length > 15 ? (value.slice(0, 15)+"...") : value;
        }
      }
    },
    yAxis: {
      type: "value"
    },
    tooltip: {
      trigger: "axis",
      axisPointer: {
        type: "shadow"
      },
      formatter: function(params) {
        const total = params.reduce((sum, p) => (sum+p.value), 0);
        const lines = params.map(p => `${p.seriesName}: ${p.value}`);
        lines.push(`<strong>Tổng: ${total}</strong>`);
        return lines.join("<br/>");
      }
    },
    legend: {
      top: 45
    },
    title: {
      text: "Số lượng Báo cáo theo Loại",
      left: "center",
      top: 15,
      textStyle: {
        fontSize: 20,
        fontWeight: "bold",
        fontFamily: "Arial, sans-serif"
      }
    },
    series: capList.map(cap => ({
      name: cap,
      type: "bar",
      stack: "total",
      label: {
        show: true,
        position: "inside",
        fontSize: 10
      },
      data: loaiList.map(loai => {
        const match = data.find(d => d.loai === loai && d.cap === cap);
        return match ? match.so_luong : 0;
      })
    })),
    grid: {
      left: 20,
      right: 20,
      bottom: 20,
      top: 80,
      containLabel: true
    }
  };
})()
))(ChartSL_LBC.chartType) }}
